mapper.select:
/* MyPageMapper.getUserOrderStatusCount */ SELECT PAYMENT_STATUS ,COUNT(PAYMENT_STATUS) AS PAYMENT_STATUS_COUNT ,ORDER_STATUS ,COUNT(ORDER_STATUS) AS ORDER_STATUS_COUNT
FROM ORDERS WHERE DATE_FORMAT(DATE_ADD(ORDER_DATE,interval 1 month),'%Y-%m-%d') AND USER_ID=#{userId}
GROUP BY PAYMENT_STATUS, ORDER_STATUS

/* MyPageMapper.getUserOrderInfoList */ SELECT ORD.ORDER_ID ,ORD.ORDER_PD_ID ,ORD.PS_INDEX ,ORD.PD_COUNT ,ORD.PD_COUNT_PRICE ,ORD.PAYMENT_STATUS ,ORD.ORDER_STATUS ,SL.SEL_NAME ,PF.PS_TITLE ,PF.UPLOAD_PATH ,PF.FILE_ID ,DATE_FORMAT(ORD.ORDER_DATE,'%Y.%m.%d') AS ORDER_DATE FROM ORDERS ORD LEFT JOIN SELLER_INFO SL ON ORD.SEL_ID = SL.SEL_ID LEFT JOIN (SELECT PP.PS_INDEX ,PP.PS_TITLE ,FL.UPLOAD_PATH ,FL.FILE_ID FROM PRODUCT_POST PP ,FILE_INFO FL WHERE PP.FILE_ID = FL.FILE_ID) PF ON ORD.PS_INDEX = PF.PS_INDEX WHERE USER_ID = #{userId}
AND DATE_FORMAT(DATE_ADD(ORD.ORDER_DATE,interval 1 month),'%Y-%m-%d')
AND (DATE_FORMAT(ord.order_date,'%Y') = #{orderDate})
AND DATE_FORMAT(DATE_ADD(ORD.ORDER_DATE,interval 1 month),'%Y-%m-%d') AND payment_status = #{orderStatus} AND order_status IS NULL
AND DATE_FORMAT(DATE_ADD(ORD.ORDER_DATE,interval 1 month),'%Y-%m-%d') AND order_status = #{orderStatus}
ORDER BY ORDER_ID DESC

mapper.update:
/* MyPageMapper.cancelOrderProcess*/
UPDATE ORDERS
SET    ORDER_STATUS = '40'
WHERE  ORDER_PD_ID = #{orderPdId}

	   /* MyPageMapper.confirmationProcess */
        UPDATE ORDERS
        SET    ORDER_STATUS = '50' 
        WHERE  ORDER_PD_ID = #{orderPdId}

mapper.delete:
/* MyPageMapper.deleteByAddress */
DELETE 
    FROM    user_address 
    WHERE   user_id=#{userId} 
      AND   addr_index=${addrIndex}

mapper.update:
/* MyPageMapper.updateAddrNo */
UPDATE  user_address
SET     addr_yn = 'N'
WHERE   USER_ID = #{userId}

mapper.insert:
/* MyPageMapper.setAddress */
INSERT INTO user_address 
VALUES(#{userId}
        ,(SELECT num
            from (SELECT IFNULL(MAX(addr_index),0)+1 as num
                    FROM user_address 
                   WHERE user_id=#{userId}) ind)
        ,#{addrName, jdbcType=VARCHAR}
        ,#{addrRecipient, jdbcType=VARCHAR}
        ,#{addrPostcode, jdbcType=VARCHAR}
        ,#{addrRoadaddr, jdbcType=VARCHAR}
        ,#{addrDetailaddr, jdbcType=VARCHAR}
        ,#{addrContact, jdbcType=VARCHAR}
        ,#{addrYn, jdbcType=CHAR})

mapper.update:
/* MyPageMapper.setUserDetails */
UPDATE  USER_INFO
SET     USER_NICKNAME = IFNULL(#{userNickname, jdbcType=VARCHAR},USER_NICKNAME)
        , USER_PHONE = IFNULL(#{userPhone, jdbcType=VARCHAR},USER_PHONE)
WHERE   USER_ID = #{userId}

mapper.select:
/*MyPageMapper.comparePw*/
SELECT  USER_PW
FROM    USER_INFO
WHERE   USER_ID = #{userId}

mapper.update:
/* MyPageMapper.setUserPw */
UPDATE  USER_INFO
SET     USER_PW = #{userPw}
WHERE   USER_ID = #{userId}
